mapper.select:
select m.id m_id, m.date m_date, m.userid m_userid, p.*, u.name u_name, t.name t_name, avg(i.score1) as score1, avg(i.score2) as score2 from markitem m 
	left join markpipe i on i.markid = m.id
	left join project p on p.id = m.projectid
	left join users u on u.id = m.userid	
	left join users t on t.id = p.userid

	
		and m.id = #{id}
	
	
		and m.projectid = #{project.id}
	
	
		and u.companyid = #{company.id}
	
	
		and u.id = #{user.id}
	
	
		and t.id = #{temp.id}
	

group by m.id order by m.id desc

select m.id m_id, m.date m_date, m.userid m_userid, p.*, u.name u_name, t.name t_name, avg(i.score1) as score1, avg(i.score2) as score2 from markitem m 
	left join markpipe i on i.markid = m.id
	left join project p on p.id = m.projectid
	left join users u on u.id = m.userid	
	left join users t on t.id = p.userid

	
		and p.name like #{name}
	
	
		and m.projectid = #{project.id}
	
	
		and u.companyid = #{company.id}
	
	
		and u.id = #{user.id}
	
	
		and t.id = #{temp.id}
	

group by m.id order by m.id desc

		select p.*, u.name u_name, count(m.id) count from project p 
  			left join markitem m on m.projectid = p.id and m.userid = #{user.id} 
			left join users u on u.id = p.userid
		
			
				and p.name like #{name}
			
			
				and p.state = #{state}
			
			
				and m.projectid = #{project.id}
			
			
				and u.companyid = #{company.id}
			
		
		group by p.id order by p.id desc
